$container = 'OU=Accounting,OU=Thanks Cinco,DC=cinco,DC=local'$userlist = Get-ADUser -Filter * -SearchBase $container | Where-object { $_.UserPrincipalName -Match '^\w+\.' } | Select -ExpandProperty UserPrincipalName | Sort-Object -Property UserPrincipalNameforeach ( $user in $userlist )	    {        		        Get-MailboxStatistics $user | Format-List DisplayName,TotalItemSize        }