$container = 'OU=DEF,OU=IT,OU=Privileged,OU=Keller Logistics Group,DC=Keller,DC=local'$userlist = Get-ADUser -Filter * -SearchBase $container | Where-object { $_.UserPrincipalName -Match '^\w+\.' } | Select -ExpandProperty UserPrincipalName | Sort-Object -Property UserPrincipalNameforeach ( $user in $userlist )	    {        		        Get-MailboxStatistics $user | Format-Table DisplayName,TotalItemSize,Database                }